package com.zzl.service.impl;

import com.zzl.service.SendMailService;
import org.springframework.beans.factory.annotation.Autowired;
import org.springframework.beans.factory.annotation.Value;
import org.springframework.mail.SimpleMailMessage;
import org.springframework.mail.javamail.JavaMailSender;
import org.springframework.mail.javamail.MimeMessageHelper;
import org.springframework.scheduling.annotation.Scheduled;
import org.springframework.stereotype.Service;

import javax.mail.MessagingException;
import javax.mail.internet.MimeMessage;
import java.io.File;

//发送简单邮件
//@Service
public class MutiSendMailServiceImpl implements SendMailService {


    @Autowired
    private JavaMailSender javaMailSender;


    //发送人
    @Value("${spring.mail.username}")
    private String from;
    //接收人
    private String to="3490596334@qq.com";
    //标题
    private String title="darling";

    //内容
//    private String content="hello,my dear son";
//    private String content="<a href='www.bilibili.com'>点开有惊喜</a>";
    private String content="<img src='data:image/jpeg;base64,'>" +
            "\n<a href='https://www.bilibili.com/video/BV15b4y1a7yG?p=122&vd_source=6e90e6694b872418823462bc7be8b73d'>点开有惊喜</a>";

//    @Scheduled(cron = "0/1 * * * * ?")
    @Override
    public void sendMail() {

        try {

            MimeMessage message=javaMailSender.createMimeMessage();
            //允许添加附件
            MimeMessageHelper helper = new MimeMessageHelper(message,true);

            helper.setFrom(from+"(你爹)");

            helper.setTo(to);
            helper.setSubject(title);

            //打开才能使用超链接
            helper.setText(content,true);


            //添加附件

            File file = new File("D:\\IntelliJ IDEA 2021.1.2\\SpringBoot\\设计模式\\src\\设计模式分类.txt");
            helper.addAttachment("设计模式",file);

            javaMailSender.send(message);

        } catch (MessagingException e) {
            e.printStackTrace();
        }


        //定时发送
//        Date date = new Date();
//        SimpleDateFormat simpleDateFormat = new SimpleDateFormat("yyyy-MM-dd hh:mm:ss");
//        System.out.println("定时设置为:"+simpleDateFormat.format(date));
//        message.setSentDate(date);




    }
}
